package com.craft.knowledges.datatype;

public class DataTypeClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int myNum = 5; // Integer (whole number)
		short myValue = 5; // short integer
		byte noOfRooms = 5; // byte integer
		long distanceToSun = 98000000L; // long integer

		long a = 6666L;
		float b = 3.3f;
		
		float myFloatNum = 5.99f; // Floating point number
		double weightOfPotato = 5.984; // double data type can take fractions

		char myLetter = 'D'; // Character

		boolean myBool = true; // Boolean

		System.out.println(myNum);
		System.out.println(myValue);
		System.out.println(noOfRooms);
		System.out.println(distanceToSun);
		System.out.println(myFloatNum);
		System.out.println(weightOfPotato);
		System.out.println(myLetter);
		System.out.println(myBool);
		
		
		
	}
}